import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../UI/Card";
import ExpenseFilter from "./ExpenseFilter";

const Expenses = ({ props }) => {
  const [filteredYear, setFilteredYear] = useState("2020");
  const [filterInfoText, setFilterInfoText] = useState("2019, 2021 and 2022")
  const filterChangeHandler = (selectYear) => {
    setFilteredYear(selectYear);
    if(selectYear === "2019") setFilterInfoText("2020, 2021 and 2022")
    else if(selectYear === "2020") setFilterInfoText("2020, 2021 and 2022")
  };

  return (
    <div>
      <Card className="expenses">
        <p>Data for {filterInfoText} is hidden</p>
        <ExpenseFilter
          selected={filteredYear}
          onChangeFilter={filterChangeHandler}
        />
        <ExpenseItem
          title={props[0].title}
          amount={props[0].amount}
          date={props[0].date}
        />

        <ExpenseItem
          title={props[1].title}
          amount={props[1].amount}
          date={props[1].date}
        />

        <ExpenseItem
          title={props[2].title}
          amount={props[2].amount}
          date={props[2].date}
        />

        <ExpenseItem
          title={props[3].title}
          amount={props[3].amount}
          date={props[3].date}
        />
      </Card>
    </div>
  );
};

export default Expenses;
